import React, { useState, useEffect } from 'react';
import DatePicker from 'react-datepicker';
import 'react-datepicker/dist/react-datepicker.css';
import '../Mainpage/Mainpage.css'; // Mainpage.css 파일을 임포트합니다.
import { Mainpage } from '../main'; // Mainpage 함수를 임포트합니다.

export const CalenderButton = ({ onClick }) => {
  return <button id="calender" onClick={onClick}>캘린더</button>;
};

const Calender1 = () => {
  const [selectedDate, setSelectedDate] = useState(new Date());
  const [showDatePicker, setShowDatePicker] = useState(false);

  useEffect(() => {
    Mainpage(); // 컴포넌트가 마운트될 때 Mainpage 함수를 호출합니다.
  }, []);

  const handleButtonClick = () => {
    setShowDatePicker(!showDatePicker);
  };

  return (
    <div className="calendar-container">
      <CalenderButton onClick={handleButtonClick} />
      {showDatePicker && (
        <DatePicker
          selected={selectedDate}
          onChange={(date) => setSelectedDate(date)}
          inline
          locale="ko"
          calendarStartDay={0}
          renderCustomHeader={({
            date,
            decreaseMonth,
            increaseMonth,
            prevMonthButtonDisabled,
            nextMonthButtonDisabled
          }) => (
            <div className="header">
              <button onClick={decreaseMonth} disabled={prevMonthButtonDisabled}>
                {"<"}
              </button>
              <span>{date.getFullYear()}년 {date.getMonth() + 1}월</span>
              <button onClick={increaseMonth} disabled={nextMonthButtonDisabled}>
                {">"}
              </button>
            </div>
          )}
        />
      )}
    </div>
  );
};

export default Calender1;
